namespace Application\Sonata\UserBundle\Entity;

use Sonata\UserBundle\Entity\BaseUser as BaseUser;
use Doctrine\ORM\Mapping as ORM;

/**
 * This file has been generated by the Sonata EasyExtends bundle ( http://sonata-project.org/easy-extends )
 *
 * References :
 *   working with object : http://www.doctrine-project.org/projects/orm/2.0/docs/reference/working-with-objects/en
 *
 * @author <yourname> <youremail>
 */

/**
 * @ORM\Entity
 * @ORM\Table(name="fos_user_user")
 */
class User extends BaseUser
{


    /**
     * @var integer
     *
     * @ORM\Column(name="id", type="integer")
     * @ORM\Id
     * @ORM\GeneratedValue(strategy="AUTO")
     */
    protected $id;


    /**
     * @ORM\OneToMany(targetEntity="DesoukOnline\MallBundle\Entity\Vendor", mappedBy="user")
     */
    protected $vendors;


    /**
     * Get id
     *
     * @return integer $id
     */
    public function getId()
    {
        return $this->id;
    }

    /**
     * @param mixed $objects
     */
    public function setVendors($objects)
    {
        $this->vendors = new ArrayCollection();
        foreach ($objects as $object) {
            $object->setUser($this);
            $this->addVendors($object);
        }
    }

    public function addVendors($object)
    {
        $this->vendors[] = $object;
        return $this;
    }

    /**
     * Remove Menu
     *
     * @param StudentSessions $object
     */
    public function removeVendors($object)
    {
        $this->vendors->removeElement($object);
    }


    /**
     * @return mixed
     */
    public function getVendors()
    {
        return $this->vendors;
    }


}